import React, { useState } from "react";
import { StyleSheet, Image, Text, View, ScrollView, Button } from 'react-native';
// import { NavigationContainer, DefaultTheme } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';


// import { createNativeStackNavigator } from '@react-navigation/native-stack';

const Tab = createBottomTabNavigator();

const MyTheme = {
  dark:true,
  colors: {
    primary: 'rgb(255, 251, 0)',
    background: 'rgb(255, 0, 0)',
    card: '#7ACFFF'
  },
};


export default function App({route, navigation}) {
  const {servant} = route.params;
  const [showAscension1, setShowAscension1] = useState(true);
  const [showAscension2, setShowAscension2] = useState(false);
  const [showAscension3, setShowAscension3] = useState(false);
  const [showAscension4, setShowAscension4] = useState(false);
  return (

    <View style={styles.container}>
      <ScrollView>
          <Text style={styles.text}>{servant.name} - <Text style={styles.capitalize}>{servant.className}</Text></Text>

          <View style={styles.btn}>
            <Button
              title="Ascension 1"
              onPress={() => {setShowAscension1(!showAscension1); setShowAscension2(null); setShowAscension3(null); setShowAscension4(null);}}
            />
            {showAscension1 ? (
            <Image style={styles.img} source={{ uri: servant.extraAssets.charaGraph.ascension[1] }}/>) : null}
          </View>

          <View style={styles.btn}>
            <Button
              title="Ascension 2"
              onPress={() => {setShowAscension2(!showAscension2); setShowAscension1(null); setShowAscension3(null); setShowAscension4(null); }}
            />
            {showAscension2 ? (
            <Image style={styles.img} source={{ uri: servant.extraAssets.charaGraph.ascension[2] }}/>) : null}
           </View>

           <View style={styles.btn}>
            <Button
              title="Ascension 3"
              onPress={() => {setShowAscension3(!showAscension3);  setShowAscension1(null);  setShowAscension2(null);  setShowAscension4(null);}}
            />
            {showAscension3 ? (
            <Image style={styles.img} source={{ uri: servant.extraAssets.charaGraph.ascension[3] }}/>) : null}
          </View>

          <View style={styles.btn}>
            <Button
              title="Ascension 4"
              onPress={() => {setShowAscension4(!showAscension4);  setShowAscension1(null);  setShowAscension2(null);  setShowAscension3(null);}}
            />
            {showAscension4 ? (
            <Image style={styles.img} source={{ uri: servant.extraAssets.charaGraph.ascension[4] }}/>) : null}
          </View>

          {/* <Image style={styles.img} source={{ uri: servant.extraAssets.charaGraph.ascension[2] }}/>
          <Image style={styles.img} source={{ uri: servant.extraAssets.charaGraph.ascension[3] }}/>
          <Image style={styles.img} source={{ uri: servant.extraAssets.charaGraph.ascension[4] }}/> */}
      </ScrollView>
   </View> 

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#1C2833',
    alignItems: 'center',
    justifyContent: 'center',
    flexDirection:"row",
  },
  // btnContainer:{
  //   flex: 1,
  //   flexDirection:"row",
  //   justifyContent: 'space-between'
  // },
  capitalize:{
    textTransform: 'capitalize'
  },

  text:{
    color:'white',
    fontSize:25,
    margin:25,
    fontWeight:'bold',
  },
  img:{
    height:300,
    width: 250,
    marginBottom: 10,
    marginTop: 10,
    alignSelf:"center"
  },
  btn:{
    marginBottom: 10,
    marginTop: 10,
    width: 150,
    alignSelf: "center"
  }
});
