import React, { useState } from "react";
import { StyleSheet, Image, Text, View, ScrollView, Button } from 'react-native';
// import { NavigationContainer, DefaultTheme } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';


// import { createNativeStackNavigator } from '@react-navigation/native-stack';

const Tab = createBottomTabNavigator();

export default function App({route, navigation}) {
  const {servant} = route.params;
  const [showAscension1, setShowAscension1] = useState(true);
  const [showAscension2, setShowAscension2] = useState(false);
  const [showAscension3, setShowAscension3] = useState(false);
  const [showAscension4, setShowAscension4] = useState(false);
  const [showCostume, setShowCostume] = useState(false);
  const costumeServant = Object.values({servant});
  const constCostume = costumeServant.filter((costume) => costume.extraAssets.charaGraph.costume === (servant.id + 30));


  return (


    <View style={styles.container}>
      <ScrollView>
          <Text style={styles.titre}>{servant.name} - <Text style={styles.capitalize}>{servant.className}</Text></Text>
          <View style={styles.btnContainer}>
            <Button
              color={couleurBtn}
              title="Ascension 1"
              onPress={() => {setShowAscension1(!showAscension1); setShowAscension2(null); setShowAscension3(null); setShowAscension4(null);}}
            />
            {showAscension1 ? (
            <Image style={styles.img} source={{ uri: servant.extraAssets.charaGraph.ascension[1] }}/>) : null}
          </View>

          <View style={styles.btnContainer}>
            <Button
              color={couleurBtn}
              title="Ascension 2"
              onPress={() => {setShowAscension2(!showAscension2); setShowAscension1(null); setShowAscension3(null); setShowAscension4(null); }}
            />
            {showAscension2 ? (
            <Image style={styles.img} source={{ uri: servant.extraAssets.charaGraph.ascension[2] }}/>) : null}
           </View>

           <View style={styles.btnContainer}>
            <Button
              color={couleurBtn}
              title="Ascension 3"
              onPress={() => {setShowAscension3(!showAscension3);  setShowAscension1(null);  setShowAscension2(null);  setShowAscension4(null);}}
            />
            {showAscension3 ? (
            <Image style={styles.img} source={{ uri: servant.extraAssets.charaGraph.ascension[3] }}/>) : null}
          </View>

          <View style={styles.btnContainer}>
            <Button
              color={couleurBtn}
              title="Ascension 4"
              onPress={() => {setShowAscension4(!showAscension4);  setShowAscension1(null);  setShowAscension2(null);  setShowAscension3(null);}}
            />
            {showAscension4 ? (
            <Image style={styles.img} source={{ uri: servant.extraAssets.charaGraph.ascension[4] }}/>) : null}
          </View>

          {/* <Image style={styles.img} source={{ uri: servant.extraAssets.charaGraph.costume[servant.id + 30] }}/> */}
          <View>
          ({constCostume.map((costumeBis) => <Text>{costumeBis}</Text>)})
          </View>
          <Text style={styles.text}>{servant.id + 30}</Text>
      </ScrollView>
   </View> 

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#1C2833',
    alignItems: 'center',
    justifyContent: 'center',
    flexDirection:"row",
  },
  capitalize:{
    textTransform: 'capitalize'
  },
  titre:{
    color:'white',
    fontSize:25,
    margin:25,
    fontWeight:'bold',
    alignSelf:"center"
  },
  text:{
    color:'white',
    fontSize:15,
    margin:25,
    alignSelf:"center"
  },
  img:{
    height:400,
    width: 250,
    marginBottom: 10,
    marginTop: 10,
    alignSelf:"center"
  },
  btnContainer:{
    marginBottom: 10,
    marginTop: 10,
    width: 150,
    alignSelf: "center"
  },
});

const couleurBtn = "#3B3B58";
