import { StatusBar } from 'expo-status-bar';
import React, { useState } from "react";
import { StyleSheet, Text, View, Image, TouchableOpacity, Modal, Alert, Pressable, ScrollView, FlatList, SafeAreaView } from 'react-native';
import servant from "../nice_servant.json";

function ServSaber(){
    const saber = servant.filter((name) => name.className ==="saber");
    return(
      <SafeAreaView style={styles.container}>
      <StatusBar style="auto" hidden={true} backgroundColor="red" animated={true} StatusBarStyle="light-content"/>
      
      <Image style={styles.img} source={require('../assets/Class-Saber-Gold.png')} />
      <FlatList
        data={saber}
        horizontal
        renderItem={({item}) => (
          <View style={styles.viewChar}>
            <Text style={styles.text}>{item.name} {[...Array(item.rarity)].map((e, i) => <Text key={i}>â˜…</Text>)} {"\n"} 
              <Text style={styles.capitalize}></Text> 
            </Text>
            
            <Image style={styles.ascension} source={{uri: item.extraAssets.charaGraph.ascension[1]}} />
        </View>
        )}
      />  
    </SafeAreaView>
    )
  }


  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#1C2833',
      alignItems: 'center',
      justifyContent: 'center',
    },
  
    text:{
      color:'black',
      fontSize:25,
      margin:25,
      fontWeight:'bold',
    },
    img:{
      height:45,
      width: 45,
      marginBottom: 10
    },
    ascension:{
      height:300,
      width: 220
      
    },
    capitalize:{
      textTransform: 'capitalize'
    },
    scrollView:{
      height:250
    },
    viewChar:{
      backgroundColor: "white",
      width: 300,
      height: 500,
      margin: 10,
      alignItems: "center",
      justifyContent: "center"
    },
    tab:{
      fontSize: 145
    }
  });

  export default Saber;
