import React, { useState } from "react";
import { StyleSheet, Image, Text, View, ScrollView, Button, FlatList } from 'react-native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';

export default function DetailsMat({route}){
    const {mat} = route.params;
    return(
        <View style={styles.container}>
            <Text style={styles.titre}>
                {mat.name}
            </Text>
            <Image style={styles.img} source={{uri: mat.icon}}/>
            <View style={styles.infos}>
                <View style={styles.infosText}><Text style={styles.text}>Type: <Text style={styles.type}>{mat.type}</Text></Text></View>
                <View style={styles.infosText}><Text style={styles.text}>{mat.detail.split('"').join(" - ")}</Text></View>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#1C2833',
      justifyContent:"center",
      alignItems:"center",
    },
    titre:{
      color:'white',
      fontSize:25,
      margin:25,
      fontWeight:'bold',
    },
    img:{
        width:128,
        height:128
    },
    infos:{
        justifyContent:"center",
        alignItems:"center",
        margin: 20
    },
    infosText:{
        borderWidth: 1,
        borderColor: "white",
        width: 350,
        alignItems:"center",
        justifyContent:"center"
    },
    text:{
        color: "#8CFBDE",
        fontSize:15,
        padding:  10
    },
    type:{
        color: "#8CFBDE",
        fontSize:15,
        padding:  10,
        textTransform:"uppercase"
    }
  });
