import {  Image, Text, View, StyleSheet} from 'react-native';
import { ScrollView } from 'react-native-gesture-handler';
// import styles from "../styles/style-mats-details"

export default function DetailsMC({route}){
    const {mysticCode} = route.params;

    const [showMale, setShowMale] = useState(true);
    return(
        <View style={styles.container}>
            <Text style={styles.titre}>
                {mysticCode.name}
            </Text>
            <ScrollView style={styles.containerMC} horizontal>
                    <Button
                    title="Male ver."
                    onPress={() => {setShowMale(!showMale)}}
                  />
                  {showMale ? (<Image style={styles.figure} source={{uri: mysticCode.extraAssets.masterFigure.male}} />): ( <Image style={styles.figure} source={{uri: mysticCode.extraAssets.masterFigure.female}} />)}
               
                
            </ScrollView>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#1C2833',
      justifyContent:"center",
      alignItems:"center",
    },
    containerMC:{
        flexDirection:"row"
    },
    titre:{
        color:'white',
        fontSize:20,
        margin:25,
        fontWeight:'bold',
      },
      figure:{
          width: 275,
          height: 300,
      }
})
