import React, { useState, useEffect } from "react";
import { StyleSheet, Image, Text, View, SafeAreaView, FlatList, TouchableOpacity } from 'react-native';
// import { NavigationContainer, DefaultTheme } from '@react-navigation/native';
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import { ScrollView } from "react-native-gesture-handler";
import DetailsMat from "./DetailsMats";

const Stack = createNativeStackNavigator();

 export default function MC({navigation}){
  const [mc, setMc] = useState([]);

    const getMc = async () => {
       try {
        const response = await fetch("https://api.atlasacademy.io/export/NA/nice_mystic_code.json");
        const json = await response.json();
        setMc(json);
      } catch (error) {
        console.error(error);
      } 
    }

    useEffect(() => {
      getMc();
    }, []);


    return (
        <View style={styles.container}>
          <FlatList
          horizontal
          data={mc}
          renderItem={({ item }) => (
            <View style={styles.mc}>
              <Text style={styles.text}>{item.name}</Text>
              <View style={styles.containerImg}>

              </View>
            </View>
          )}/>
        </View>
    )
}

