import {  Image, Text, View, ScrollView} from 'react-native';
import styles from "../styles/style-ce-details"
import React, { useState, useEffect } from "react"

export default function DetailsCE({route}){
    const {CE} = route.params;
    
    const [servCE, setServCE] = useState([]);
  
    const getServCE = async () => {
       try {
        const response = await fetch('https://api.atlasacademy.io/export/NA/nice_servant.json');
        const json = await response.json();
        setServCE(json.filter((serv) =>serv.id === CE.bondEquipOwner));
      } catch (error) {
        console.error(error);
      } 
    }
  
    useEffect(() => {
      getServCE();
    }, []);

    console.log(servCE.extraAssets);
    return(
        <ScrollView contentContainerStyle={styles.container}>
            <Text style={styles.titre}>
                {CE.name}
            </Text>
            <Image style={styles.img} source={{uri : CE.extraAssets.charaGraph.equip[CE.id]}}/>

            <View style={styles.containerInfos}>
            {/* <Image style={styles.iconPerso} source={{uri : servCE.extraAssets[0].faces.ascension[1]}}/> */}
                <View style={styles.infos}>
                    <Text style={styles.cost}>Cost: {CE.cost}, Lv. max {CE.lvMax}</Text>
                </View>
                <View style={styles.infos}>
                    <Text style={styles.text}>{CE.skills[0].detail}</Text>
                </View>
            </View>
        </ScrollView>  
    )
}
