import { StyleSheet} from "react-native";
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#1C2833',
      alignItems: 'center',
      justifyContent: 'center',
      flexDirection:"row",
    },
    capitalize:{
      textTransform: 'capitalize'
    },
    titre:{
      color:'white',
      fontSize:25,
      margin:25,
      fontWeight:'bold',
      alignSelf:"center"
    },
    text:{
      color:'white',
      fontSize:15,
      margin:25,
      alignSelf:"center"
    },
    desc:{
        color:'white',
        fontSize:15,
        margin:25,
        alignSelf:"center"
    },
    img:{
      height:400,
      width: 250,
      marginBottom: 10,
      marginTop: 10,
      alignSelf:"center"
    },
    btnContainer:{
      marginBottom: 10,
      marginTop: 10,
      width: 150,
      alignSelf: "center"
    },
    separation:{
      alignSelf: "center",
      fontWeight: "bold",
      fontSize: 15,
      color: "white",
      marginBottom: -10
    },
    skill:{
        alignItems: "center"
    },
    containerSkill:{
        borderColor: "white",
        borderWidth: 1,
        marginBottom: 10,
        marginLeft: 10,
        alignItems: "center",
        justifyContent: "center",
        width: 350
    },
    containerImgName:{
        flexDirection:"row",
        alignItems: "center",
        justifyContent: "center",
    },
    imgSkill:{
        width:50,
        height: 50,
    }
  
  });

  export default styles;
