import React, { useState, useEffect } from "react";
import { StyleSheet, Image, Text, View, SafeAreaView, FlatList } from 'react-native';
// import { NavigationContainer, DefaultTheme } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { ScrollView } from "react-native-gesture-handler";


// import { createNativeStackNavigator } from '@react-navigation/native-stack';

const Tab = createBottomTabNavigator();

export default function Mats({navigation}){
  const [mats, setMats] = useState([]);

    const getMats = async () => {
       try {
        const response = await fetch('https://api.atlasacademy.io/export/NA/nice_item.json');
        const json = await response.json();
        setMats(json.filter((filtre) => filtre.name.length < 10 ) );
      } catch (error) {
        console.error(error);
      } 
    }

    useEffect(() => {
      getMats();
    }, []);

    return (
      <SafeAreaView style={styles.view}>
        <ScrollView style={styles.container} horizontal>

          <FlatList
          data={mats}
          numColumns={2}
          renderItem={({ item }) => (
            <View style={styles.containerItem}>
              <Text style={styles.text}>{item.name}</Text>
              <Image style={styles.img} source={{ uri: item.icon}}
                />
            </View>
          )}/>

        </ScrollView>
      </SafeAreaView>
    )
}

const styles = StyleSheet.create({
  view:{

  },
  container: {
    flex: 1,
    backgroundColor: '#1C2833',
  },
  containerItem:{
    backgroundColor:"rgba(98, 121, 184, 0.5)",  
    margin: 5,
    width: 150,
    alignItems:"center",
    justifyContent:"center"
  },
  text:{
    color:'black',
    fontSize:15,
    margin:25,
    fontWeight:'bold',
  },
  img:{
    width: 64,
    height: 64,
    marginBottom: 10
  }
});
