import React, { useState } from "react";
import { StyleSheet, Image, Text, View, ScrollView, Button, FlatList } from 'react-native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';

export default function DetailsMat({route}){
    const {mat} = route.params;
    return(
        <View style={styles.container}>
            <Text style={styles.titre}>
                {mat.name}
            </Text>
            <Image style={styles.img} source={{uri: mat.icon}}/>
            <View style={styles.infos}>
                <Text style={styles.text}>{mat.detail.replace('"', ' - ')}</Text>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#1C2833',
      justifyContent:"center",
      alignItems:"center",
    },
    titre:{
      color:'white',
      fontSize:25,
      margin:25,
      fontWeight:'bold',
    },
    img:{
        width:128,
        height:128
    },
    infos:{
        flexDirection:"row",
        justifyContent:"center",
        alignItems:"center",
        margin: 20
    },
    text:{
        color: "white",
        fontSize:15
    }
  });
