import React, { useState, useEffect } from "react";
import { StyleSheet, Image, Text, View, SafeAreaView, FlatList } from 'react-native';
// import { NavigationContainer, DefaultTheme } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { ScrollView } from "react-native-gesture-handler";
import App from "./DetailsPerso";


// import { createNativeStackNavigator } from '@react-navigation/native-stack';

const Tab = createBottomTabNavigator();

 function Mats({navigation}){
  const [mats, setMats] = useState([]);

    const getMats10 = async () => {
       try {
        const response = await fetch('https://api.atlasacademy.io/export/NA/nice_item.json');
        const json = await response.json();
        setMats(json.filter((filtre) => filtre.name.length < 10 ) );
      } catch (error) {
        console.error(error);
      } 
    }

    useEffect(() => {
      getMats10();
    }, []);

    return (

          <FlatList
          data={mats}
          numColumns={2}
          renderItem={({ item }) => (
            <View style={styles.containerItem}>
              <Text style={styles.text}>{item.name}</Text>
              <Image style={styles.img} source={{ uri: item.icon}}
                />
            </View>
          )}/>

    )
}
export default function App2(){
  return(
    <ScrollView contentContainerStyle={styles.container} horizontal>
      <View style={styles.view}>
        <Mats/>
      </View>
    </ScrollView>
  )
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#1C2833',
    justifyContent:"center",
    alignItems:"center",
  },
  containerItem:{
    backgroundColor:"rgba(8, 124, 167, 0.3)",  
    margin: 5,
    width: 150,
    alignItems:"center",
    justifyContent:"center",
    borderColor: "white",
    borderRadius: 20,
    borderLeftWidth: 1,
    borderRightWidth: 1
  },
  view:{
    justifyContent:"center",
    alignItems:"center"
  },
  text:{
    color:'white',
    fontSize:15,
    margin:25,
    fontWeight:'bold',
  },
  img:{
    width: 64,
    height: 64,
    marginBottom: 10
  }
});
