  Text,
  View,
  Image,
  TouchableOpacity,
  Modal,
  Alert,
  Pressable,
  ScrollView,
  FlatList,
  SafeAreaView,
} from "react-native";

export default function App() {
  return (
    <SafeAreaView style={styles.BG}>
      <View>
        <StatusBar hidden />
        <Text style={styles.DB}>FGO DATABASE</Text>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
