import { StatusBar } from 'expo-status-bar';
import React, { useState } from "react";
import { StyleSheet, Text, View, Image, TouchableOpacity, Modal, Alert, Pressable, ScrollView, FlatList, SafeAreaView } from 'react-native';
import { NavigationContainer, DefaultTheme } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';

//Import des diff√©rentes classes
import Saber from './classes/saber';
import Archer from './classes/archer';

// import { createNativeStackNavigator } from '@react-navigation/native-stack';



function SettingsScreen() {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>Settings!</Text>
    </View>
  );
}

const Tab = createBottomTabNavigator();

const MyTheme = {
  dark:true,
  colors: {
    primary: 'rgb(255, 251, 0)',
    background: 'rgb(255, 0, 0)',
    card: '#7ACFFF'
  },
};

// const screenOptions = {
//   tabBarStyle:{
//     backgroundColor:'#0000ff',
//     height:100,
//   },
//   tabBarItemStyle:{
//     backgroundColor:'#00ff00',
//     margin:5,
//     borderRadius:10,
//   }
// }

export default function App() {
  return (
    <NavigationContainer style={styles.tab}>
      <Tab.Navigator
        screenOptions={({ route }) => ({
          tabBarIcon: ({focused}) => {
            let url;

            if (route.name === "Saber") {
              url = focused ? require('../FGO_DB/assets/Class-Saber-Gold.png'): require('../FGO_DB/assets/Class-Saber-Silver.png');
            }
            else if(route.name === "Archer"){
              url = focused ? require('../FGO_DB/assets/Class-Archer-Gold.png'): require('../FGO_DB/assets/Class-Archer-Silver.png');
            }

            return <Image style={styles.img} source={url} />;
          },
          tabBarActiveTintColor: "#FFD700",
          tabBarInactiveTintColor: "white",
          headerShown: false,
          tabBarStyle:{
            paddingVertical: 10,
            borderTopLeftRadius:30,
            borderTopRightRadius:30,
            borderColor: "#0F151B",
            backgroundColor:'#397AB6',
            position:'absolute',
            height:80,
          },
          tabBarLabelStyle:{
            fontSize: 15
          }
        })}

      >
        <Tab.Screen name="Saber" component={Saber} style={styles.tab}/>
        <Tab.Screen name="Archer" component={Archer} />
      </Tab.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#1C2833',
    alignItems: 'center',
    justifyContent: 'center',
  },

  text:{
    color:'black',
    fontSize:25,
    margin:25,
    fontWeight:'bold',
  },
  img:{
    height:45,
    width: 45,
    marginBottom: 10
  },
  ascension:{
    height:300,
    width: 220

  },
  capitalize:{
    textTransform: 'capitalize'
  },
  scrollView:{
    height:250
  },
  viewChar:{
    backgroundColor: "white",
    width: 300,
    height: 500,
    margin: 10,
    alignItems: "center",
    justifyContent: "center"
  },
  tab:{
    fontSize: 145
  }
});
